import React from "react";

export default class Contact extends React.Component {
  state = { name: "", email: "", message: "", submitted: false };

  handleChange = (e) => this.setState({ [e.target.name]: e.target.value });

  handleSubmit = (e) => {
    e.preventDefault();
    // Normally you'd call an API here. We'll just show a success message.
    this.setState({ submitted: true, name: "", email: "", message: "" });
    setTimeout(() => this.setState({ submitted: false }), 4000);
  };

  render() {
    const { name, email, message, submitted } = this.state;
    return (
      <section>
        <h2 className="section-title">Contact</h2>

        {submitted && (
          <div className="alert alert-success">Message sent â€” thank you!</div>
        )}

        <form onSubmit={this.handleSubmit} className="row g-3">
          <div className="col-md-6">
            <label className="form-label">Name</label>
            <input
              name="name"
              value={name}
              onChange={this.handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Email</label>
            <input
              name="email"
              value={email}
              onChange={this.handleChange}
              type="email"
              className="form-control"
              required
            />
          </div>

          <div className="col-12">
            <label className="form-label">Message</label>
            <textarea
              name="message"
              value={message}
              onChange={this.handleChange}
              className="form-control"
              rows="5"
              required
            />
          </div>

          <div className="col-12">
            <button type="submit" className="btn btn-primary">
              Send Message
            </button>
          </div>
        </form>
      </section>
    );
  }
}
