import React from "react";
import styles from "../css/customStyle.module.css";

export default function ProjectCard({ title, desc, tags, link }) {
  // destructuring props above; this component is purely presentational
  return (
    <div className={`card card-project ${styles.cardRoot}`}>
      <img
        src="/placeholder-project.jpg"
        alt={title}
        className={`card-img-top ${styles.cardMedia}`}
      />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">{desc}</p>
        <div className="mb-2">
          {tags.map((t, i) => (
            <span className="tag" key={i}>
              {t}
            </span>
          ))}
        </div>
        <a href={link} className="btn btn-sm btn-outline-primary">
          View
        </a>
      </div>
    </div>
  );
}
