// About.js (only the profile card part - paste into your existing About component)
import React, { useRef } from "react";
import { FaGithub, FaLinkedin, FaTwitter } from "react-icons/fa";
import { smoothScroll } from "../utils/scroll"; // if you need the resume link to scroll somewhere

export default function AboutProfileBlock() {
  const cardRef = useRef(null);
  const animRef = useRef(null);

  const MAX_TILT = 12; // degrees
  const MAX_Z = 18; // px for translateZ depth

  function handleMove(e) {
    const card = cardRef.current;
    if (!card) return;
    const rect = card.getBoundingClientRect();
    const cx = rect.left + rect.width / 2;
    const cy = rect.top + rect.height / 2;
    const relX = (e.clientX - cx) / (rect.width / 2); // -1 .. 1
    const relY = (e.clientY - cy) / (rect.height / 2); // -1 .. 1

    // clamp
    const ry = Math.max(-1, Math.min(1, relX));
    const rx = Math.max(-1, Math.min(1, relY));

    const rotateY = ry * MAX_TILT; // left/right
    const rotateX = -rx * (MAX_TILT * 0.7); // up/down (negated)
    const tz = (Math.abs(rx) + Math.abs(ry)) * (MAX_Z * 0.6);

    if (animRef.current) cancelAnimationFrame(animRef.current);
    animRef.current = requestAnimationFrame(() => {
      card.style.transform = `perspective(1200px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateZ(${tz}px)`;
      const img = card.querySelector(".profile-img");
      if (img) img.style.transform = `translateZ(${tz * 1.4}px) scale(1.01)`;
      const glow = card.querySelector(".profile-glow");
      if (glow) glow.style.transform = `translateZ(${tz * 0.6}px)`;
    });
  }

  function handleLeave() {
    if (animRef.current) cancelAnimationFrame(animRef.current);
    animRef.current = requestAnimationFrame(() => {
      const card = cardRef.current;
      card.style.transform = ""; // back to CSS default (the static tilt)
      const img = card.querySelector(".profile-img");
      if (img) img.style.transform = "";
      const glow = card.querySelector(".profile-glow");
      if (glow) glow.style.transform = "";
    });
  }

  return (
    <div
      className="about-profile p-3"
      ref={cardRef}
      onMouseMove={handleMove}
      onMouseLeave={handleLeave}
      role="region"
      aria-label="profile card"
    >
      {/* decorative layer / glow */}
      <div className="profile-glow" aria-hidden="true" />

      {/* profile image */}
      <img
        src="/jhero.jpg"
        alt="Profile"
        className="img-fluid profile-img mb-3"
      />

      <h3 className="fw-bold">Your Name</h3>
      <p className="text-info">Web Developer</p>

      <div className="d-flex justify-content-center gap-3 my-3">
        <a href="#" className="text-light" aria-label="github">
          <FaGithub size={20} />
        </a>
        <a href="#" className="text-light" aria-label="linkedin">
          <FaLinkedin size={20} />
        </a>
        <a href="#" className="text-light" aria-label="twitter">
          <FaTwitter size={20} />
        </a>
      </div>

      <div className="d-grid">
        <a href="/resume.pdf" className="btn btn-outline-light mt-3">
          Download Resume
        </a>
      </div>
    </div>
  );
}
