// src/utils/scroll.js
import { useEffect } from "react";

// Smooth scroll for nav clicks
export function smoothScroll(e, id) {
  e.preventDefault();
  const target = document.getElementById(id);
  if (target) {
    const targetPosition = target.offsetTop - 60; // adjust for navbar height
    const startPosition = window.scrollY;
    const distance = targetPosition - startPosition;
    const duration = 1000; // ms
    let startTime = null;

    const easeInOutCubic = (t) =>
      t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;

    function animation(currentTime) {
      if (startTime === null) startTime = currentTime;
      const timeElapsed = currentTime - startTime;
      const progress = Math.min(timeElapsed / duration, 1);
      const run = startPosition + distance * easeInOutCubic(progress);
      window.scrollTo(0, run);
      if (timeElapsed < duration) requestAnimationFrame(animation);
    }

    requestAnimationFrame(animation);
  }
}

// React hook for section-by-section smooth scrolling
export default function useSmoothScroll() {
  useEffect(() => {
    const sections = document.querySelectorAll("section, footer");
    let isScrolling = false;

    const handleWheel = (e) => {
      if (isScrolling) return;

      const direction = e.deltaY > 0 ? 1 : -1;
      const currentSection = Math.round(window.scrollY / window.innerHeight);
      let nextSection = currentSection + direction;

      if (nextSection < 0) nextSection = 0;
      if (nextSection >= sections.length) nextSection = sections.length - 1;

      isScrolling = true;
      sections[nextSection].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });

      setTimeout(() => {
        isScrolling = false;
      }, 300000); // match animation duration
    };

    window.addEventListener("wheel", handleWheel, { passive: false });
    return () => window.removeEventListener("wheel", handleWheel);
  }, []);
}
