import React, { useRef } from "react";
import { FaLinkedin, FaGithub, FaTwitter } from "react-icons/fa";

export default function About() {
  const cardRef = useRef(null);
  const animRef = useRef(null);

  const MAX_TILT = 12; // degrees
  const MAX_Z = 18; // px depth

  function handleMove(e) {
    const card = cardRef.current;
    if (!card) return;
    const rect = card.getBoundingClientRect();
    const cx = rect.left + rect.width / 2;
    const cy = rect.top + rect.height / 2;
    const relX = (e.clientX - cx) / (rect.width / 2); // -1 .. 1
    const relY = (e.clientY - cy) / (rect.height / 2); // -1 .. 1

    const ry = Math.max(-1, Math.min(1, relX));
    const rx = Math.max(-1, Math.min(1, relY));

    const rotateY = ry * MAX_TILT;
    const rotateX = -rx * (MAX_TILT * 0.7);
    const tz = (Math.abs(rx) + Math.abs(ry)) * (MAX_Z * 0.6);

    if (animRef.current) cancelAnimationFrame(animRef.current);
    animRef.current = requestAnimationFrame(() => {
      card.style.transform = `perspective(1200px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateZ(${tz}px)`;
      const img = card.querySelector(".profile-img");
      if (img) img.style.transform = `translateZ(${tz * 1.4}px) scale(1.01)`;
      const glow = card.querySelector(".profile-glow");
      if (glow) glow.style.transform = `translateZ(${tz * 0.6}px)`;
    });
  }

  function handleLeave() {
    if (animRef.current) cancelAnimationFrame(animRef.current);
    animRef.current = requestAnimationFrame(() => {
      const card = cardRef.current;
      card.style.transform = "";
      const img = card.querySelector(".profile-img");
      if (img) img.style.transform = "";
      const glow = card.querySelector(".profile-glow");
      if (glow) glow.style.transform = "";
    });
  }

  return (
    <section id="about" className="min-vh-100 bg-dark text-light py-5">
      <div className="container">
        <div className="row">
          {/* Left Side - Profile Card */}
          <div className="col-md-4 text-center mb-4">
            <div
              className="about-profile p-3"
              ref={cardRef}
              onMouseMove={handleMove}
              onMouseLeave={handleLeave}
            >
              {/* Glow layer */}
              <div className="profile-glow" aria-hidden="true" />

              <img
                src="/jhero.jpg"
                alt="Profile"
                className="img-fluid profile-img mb-3"
              />
              <h3 className="fw-bold">Your Name</h3>
              <p className="text-info">Web Developer</p>

              {/* Social Links */}
              <div className="d-flex justify-content-center gap-3 my-3">
                <a href="#" className="text-light" aria-label="Github">
                  <FaGithub size={20} />
                </a>
                <a href="#" className="text-light" aria-label="LinkedIn">
                  <FaLinkedin size={20} />
                </a>
                <a href="#" className="text-light" aria-label="Twitter">
                  <FaTwitter size={20} />
                </a>
              </div>

              {/* Resume Button */}
              <a href="/resume.pdf" className="btn btn-outline-light mt-3">
                Download Resume
              </a>
            </div>
          </div>

          {/* Right Side - Details */}
          <div className="col-md-8">
            {/* About Me */}
            <h2 className="section-title">About Me</h2>
            <p>
              Hello! I’m [Your Name]. I have experience in web development,
              React, and UI design. I love building clean and functional web
              apps that create a great user experience.
            </p>

            {/* Personal Info */}
            <div className="row my-4">
              <div className="col-md-6">
                <p>
                  <strong>Age:</strong> 24
                </p>
                <p>
                  <strong>Residence:</strong> USA
                </p>
              </div>
              <div className="col-md-6">
                <p>
                  <strong>Freelance:</strong> Available
                </p>
                <p>
                  <strong>Address:</strong> California, USA
                </p>
              </div>
            </div>

            {/* My Services */}
            <h3 className="section-title">My Services</h3>
            <div className="row g-4 mt-2">
              <div className="col-md-6">
                <div className="service-box p-3 h-100">
                  <h5>Web Development</h5>
                  <p>
                    Build responsive, modern, and fast websites using React,
                    JavaScript, and CSS.
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="service-box p-3 h-100">
                  <h5>UI/UX Design</h5>
                  <p>
                    Design clean and user-friendly interfaces with a focus on
                    usability and accessibility.
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="service-box p-3 h-100">
                  <h5>API Integration</h5>
                  <p>
                    Connect applications with REST APIs, handle data fetching,
                    and state management.
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="service-box p-3 h-100">
                  <h5>Consultation</h5>
                  <p>
                    Provide advice on project architecture, best practices, and
                    optimization strategies.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
