import React from "react";

export default class About extends React.Component {
  state = { showMore: false };

  toggle = () => this.setState((s) => ({ showMore: !s.showMore }));

  render() {
    const { showMore } = this.state; // destructuring state
    return (
      <section >
        <h2 className="section-title">About Me</h2>
        <p>
          I'm a frontend developer focusing on React applications. I enjoy
          building UIs that are accessible and enjoyable to use.
        </p>

        {showMore && (
          <div className="card p-3 my-3">
            <p>
              Extra details: experience with JS, React, API integration,
              testing, and styling.
            </p>
          </div>
        )}

        <button className="btn btn-outline-secondary" onClick={this.toggle}>
          {showMore ? "Show less" : "Show more"}
        </button>
      </section>
    );
  }
}
