import React from "react";

export default function Header() {
  const handleScroll = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (target) {
      const targetPosition = target.offsetTop - 60; // adjust for navbar height
      const startPosition = window.scrollY;
      const distance = targetPosition - startPosition;
      const duration = 1000;
      let startTime = null;

      const easeInOutCubic = (t) =>
        t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;

      function animation(currentTime) {
        if (startTime === null) startTime = currentTime;
        const timeElapsed = currentTime - startTime;
        const progress = Math.min(timeElapsed / duration, 1);
        const run = startPosition + distance * easeInOutCubic(progress);
        window.scrollTo(0, run);
        if (timeElapsed < duration) requestAnimationFrame(animation);
      }

      requestAnimationFrame(animation);
    }
  };

  return (
    <header>
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm fixed-top py-3">
        <div className="container d-flex align-items-center">
          {/* Brand on the left */}
          <a
            className="navbar-brand fw-bold"
            href="#home"
            onClick={(e) => handleScroll(e, "home")}
          >
            MyPortfolio
          </a>

          {/* Centered Nav */}
          <div className="d-none d-lg-flex mx-auto">
            <ul className="navbar-nav gap-4">
              <li className="nav-item">
                <a
                  className="nav-link"
                  href="#home"
                  onClick={(e) => handleScroll(e, "home")}
                >
                  Home
                </a>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link"
                  href="#about"
                  onClick={(e) => handleScroll(e, "about")}
                >
                  About
                </a>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link"
                  href="#projects"
                  onClick={(e) => handleScroll(e, "projects")}
                >
                  Projects
                </a>
              </li>
            </ul>
          </div>

          {/* Contact button on the right */}
          <div className="ms-auto">
            <a
              href="#contact"
              className="btn btn-primary contact-btn"
              onClick={(e) => handleScroll(e, "contact")}
            >
              Contact Me
            </a>
          </div>
        </div>
      </nav>
    </header>
  );
}
